import React, { useState, useEffect } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import axios from "axios";

const LetterEditor = () => {
    const [content, setContent] = useState("");
    const [savedDrafts, setSavedDrafts] = useState([]);

    // Load drafts from backend
    useEffect(() => {
        axios.get("http://localhost:5000/api/drafts", { withCredentials: true })
            .then(res => setSavedDrafts(res.data))
            .catch(err => console.error(err));
    }, []);

    // Save draft
    const handleSaveDraft = () => {
        axios.post("http://localhost:5000/api/drafts", { content }, { withCredentials: true })
            .then(res => {
                setSavedDrafts([res.data.draft, ...savedDrafts]);
                alert("Draft saved!");
            })
            .catch(err => console.error(err));
    };

    return (
        <div>
            <h2>Letter Editor</h2>
            <ReactQuill value={content} onChange={setContent} />
            <button onClick={handleSaveDraft}>Save Draft</button>

            <h3>Saved Drafts:</h3>
            <ul>
                {savedDrafts.map(draft => (
                    <li key={draft._id} dangerouslySetInnerHTML={{ __html: draft.content }} />
                ))}
            </ul>
        </div>
    );
};

export default LetterEditor;
