import React, { useState } from "react";
import GoogleLogin from "./googleAuth";
import LetterEditor from "./components/LetterEditor";

function App() {
  const [savedLetter, setSavedLetter] = useState("");

  return (
    <div style={{ textAlign: "center", maxWidth: "800px", margin: "auto" }}>
      <h1>Letter Editor App</h1>
      <GoogleLogin />
      <LetterEditor onSave={setSavedLetter} />
      <h3>Saved Draft:</h3>
      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          minHeight: "100px",
          marginTop: "10px",
          textAlign: "left",
        }}
        dangerouslySetInnerHTML={{ __html: savedLetter }}
      />
    </div>
  );
}

export default App;
